import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';

function HeroSection() {
  return (
    <div className='hero-container'>
      <video src='/videos/video-1.mp4' autoPlay loop muted />
      <h3 className='hero'>Hello, my name is </h3>
      <h1 className=''>Alicia Gorton</h1>
      <p className=''>I'm a full-stack MERN web developer based in Sacramento, CA that enjoys crafting websites, applications, and everything in between. I am looking for opportunities of growth in the fields of emerging technology and data science. </p>
      <div className='hero-btns'>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
        >
          Get In Touch
        </Button>
      </div>
    </div>
  );
}

export default HeroSection;
